        [TestMethod]
        public void Part2Examples()
        {
            Assert.AreEqual(1, basement(")"));
            Assert.AreEqual(5, basement("()())"));
        }

        [TestMethod]
        public void Part2()
        {
            Console.WriteLine(basement(inputString));
        }

        private int basement(string str)
        {
            int floor = 0;
            int step = 1;
            foreach (char c in str)
            {
                switch (c)
                {
                    case '(':
                        floor++;
                        break;
                    case ')':
                        floor--;
                        break;
                    default:
                        break;
                }
                if (floor == -1)
                {
                    return step;
                }
                step++;
            }
            throw new Exception("basement not reached");
        }

        private static int walk(string str)
        {
            int result = 0;
            
            foreach (char c in str)
            {
                switch (c)
                {
                    case '(':
                        result++;
                        break;
                    case ')':
                        result--;
                        break;
                    default:
                        break;
                }
            }

            return result;
        }
    }
